import React, { useState, useEffect } from 'react';
import TrafficMap from './TrafficMap';
import CameraFeed from './CameraFeed';
import ComplaintForm from './ComplaintForm';
import Analytics from './Analytics';
import Controls from './Controls';
import StatusAlerts from './StatusAlerts';
import '../styles/Dashboard.css';

const Dashboard = () => {
  const [trafficData, setTrafficData] = useState(null);
  const [selectedCamera, setSelectedCamera] = useState(null);
  const [analytics, setAnalytics] = useState(null);
  const [alerts, setAlerts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // Mock data fetch - replace with actual API calls
  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);

      // Simulate API calls
      try {
        // Traffic data
        const trafficResponse = await fetch('/api/traffic');
        const trafficData = await trafficResponse.json();
        setTrafficData(trafficData);

        // Analytics data
        const analyticsResponse = await fetch('/api/analytics');
        const analyticsData = await analyticsResponse.json();
        setAnalytics(analyticsData);

        // Alerts
        const alertsResponse = await fetch('/api/alerts');
        const alertsData = await alertsResponse.json();
        setAlerts(alertsData);

        // Cameras
        const camerasResponse = await fetch('/api/cameras');
        const camerasData = await camerasResponse.json();
        if (camerasData.length > 0) {
          setSelectedCamera(camerasData[0]);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();

    // Set up polling (every 10 seconds)
    const interval = setInterval(fetchData, 10000);

    return () => clearInterval(interval);
  }, []);

  const handleComplaintSubmit = async (complaint) => {
    try {
      const response = await fetch('/api/complaints', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(complaint)
      });

      if (!response.ok) throw new Error('Failed to submit complaint');

      // Show success message
      setAlerts(prev => [...prev, {
        id: Date.now(),
        type: 'success',
        message: 'Complaint submitted successfully!'
      }]);
    } catch (error) {
      setAlerts(prev => [...prev, {
        id: Date.now(),
        type: 'error',
        message: error.message
      }]);
    }
  };

  const handleLightControl = async (params) => {
    try {
      const response = await fetch('/api/control', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(params)
      });

      if (!response.ok) throw new Error('Failed to adjust lights');

      setAlerts(prev => [...prev, {
        id: Date.now(),
        type: 'success',
        message: 'Traffic lights adjusted successfully'
      }]);
    } catch (error) {
      setAlerts(prev => [...prev, {
        id: Date.now(),
        type: 'error',
        message: error.message
      }]);
    }
  };

  if (isLoading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <div className="logo">
          <img src="/traffic-icon.png" alt="Traffic Icon" />
          <h1>Smart Traffic Management System</h1>
        </div>
        <div className="header-info">
          <span>Last updated: {new Date().toLocaleTimeString()}</span>
        </div>
      </header>

      <StatusAlerts alerts={alerts} onDismiss={(id) => 
        setAlerts(prev => prev.filter(alert => alert.id !== id))
      } />

      <main className="dashboard-content">
        <section className="map-section">
          <TrafficMap data={trafficData} />
        </section>

        <aside className="sidebar">
          <div className="sidebar-section camera-section">
            <CameraFeed 
              camera={selectedCamera} 
              onSelectCamera={setSelectedCamera} 
            />
          </div>

          <div className="sidebar-section analytics-section">
            <Analytics data={analytics} />
          </div>

          <div className="sidebar-section controls-section">
            <Controls onAdjustLights={handleLightControl} />
          </div>
        </aside>

        <section className="complaint-section">
          <ComplaintForm onSubmit={handleComplaintSubmit} />
        </section>
      </main>

      <footer className="dashboard-footer">
        <p>IoT-Driven Smart Traffic Management System Â© {new Date().getFullYear()}</p>
        <p>System Status: <span className="status-active">Operational</span></p>
      </footer>
    </div>
  );
};

export default Dashboard;
