import React, { useState, useEffect } from 'react';
import TrafficMap from './TrafficMap';
import Analytics from './Analytics';
import Controls from './Controls';
import '../styles/Dashboard.css';

const Dashboard = () => {
  const [trafficData, setTrafficData] = useState(null);
  const [analytics, setAnalytics] = useState(null);

  useEffect(() => {
    // Mock data fetch
    const fetchData = async () => {
      setTrafficData({
        intersections: [
          { id: 1, lat: 51.505, lng: -0.09, name: "Main Intersection", congestion: 0.7 },
          { id: 2, lat: 51.51, lng: -0.1, name: "Central Square", congestion: 0.4 },
          { id: 3, lat: 51.515, lng: -0.09, name: "North Junction", congestion: 0.9 }
        ]
      });

      setAnalytics({
        average_speed: 32,
        target_speed: 40,
        average_wait_time: 45,
        target_wait_time: 30,
        congestion_level: 0.65,
        system_uptime: 99.8
      });
    };

    fetchData();
  }, []);

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>Traffic Management Dashboard</h1>
      </header>

      <main className="dashboard-content">
        <section className="map-section">
          <TrafficMap data={trafficData} />
        </section>

        <aside className="sidebar">
          <div className="analytics-section">
            <Analytics data={analytics} />
          </div>

          <div className="controls-section">
            <Controls />
          </div>
        </aside>
      </main>
    </div>
  );
};

export default Dashboard;
